mapper.select:
SELECT SEQ, BOARD_CODE, EMAIL, TITLE, CONTENT, FILENAME, LOCATION, LOCATION_DETAIL, CATEGORY, PRICE, PEOPLE, PERSONCOUNT, READCOUNT, LIKECOUNT, REPORTCOUNT, PDATE, EDATE, REGDATE, DEL FROM (SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC)AS RNUM, SEQ, BOARD_CODE, EMAIL, TITLE, CONTENT, FILENAME, LOCATION, LOCATION_DETAIL, CATEGORY, PRICE, PEOPLE, PERSONCOUNT, READCOUNT, LIKECOUNT, REPORTCOUNT, PDATE, EDATE, REGDATE, DEL FROM PM_PLAYBOARD WHERE 1=1 AND CATEGORY LIKE '%'||#{playCategory}||'%'
)
AND DEL=1
AND EDATE <= SYSDATE
AND EMAIL LIKE '%'||#{searchText}||'%'
AND TITLE LIKE '%'||#{searchText}||'%'
AND CONTENT LIKE '%'||#{searchText}||'%'
AND LOCATION LIKE '%'||#{searchText}||'%'
)
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.delete:
DELETE
FROM PM_BOARD_REPORT
WHERE SEQ=#{seq} AND BOARD_CODE=#{board_code} AND BOARD_SEQ=#{board_seq}

mapper.update:
UPDATE PM_MEMBER
SET REPORTCOUNT=REPORTCOUNT-1
WHERE EMAIL=#{bad_email}

mapper.select:
SELECT COUNT(*)
FROM PM_LEAVEMEMBER
WHERE EMAIL=#{email}

SELECT COUNT(*)
FROM PM_MYPROFILE
WHERE EMAIL=#{email}

SELECT COUNT(*)
FROM PM_PETPROFILE
WHERE EMAIL=#{email}

SELECT * FROM(SELECT ROW_NUMBER()OVER(ORDER BY REGDATE DESC)AS RNUM, SEQ, BOARD_CODE, EMAIL, TITLE, INTRO, CONTENT, THUMBNAIL, MAX_PRICE, CURRENT_PRICE, PERSONCOUNT, LIKECOUNT, READCOUNT, SDATE, EDATE, REGDATE FROM PM_FUNDINGBOARD WHERE 1=1
AND EDATE >= SYSDATE AND CURRENT_PRICE < MAX_PRICE
AND EDATE < SYSDATE OR CURRENT_PRICE = MAX_PRICE
) WHERE RNUM BETWEEN ${start} AND ${end}

SELECT SEQ, BOARD_CODE, EMAIL, TITLE, INTRO, CONTENT, THUMBNAIL, MAX_PRICE, CURRENT_PRICE, PERSONCOUNT, LIKECOUNT, READCOUNT, SDATE, EDATE, REGDATE
FROM PM_FUNDINGBOARD
WHERE SEQ=#{seq}

SELECT F.MYPROFILE_IMG, G.NICKNAME, G.EMAIL, M.DONATION, M.D_DATE, G.NAME, M.FUNDING_SEQ
FROM PM_MYPROFILE F,PM_FUNDINGMEM M , PM_MEMBER G
WHERE F.EMAIL = M.EMAIL AND M.EMAIL = G.EMAIL AND M.FUNDING_SEQ = #{funding_seq}

mapper.insert:
INSERT INTO PM_SENDMSG(SEQ, TO_EMAIL, FROM_EMAIL, CONTENT, SENDDATE, READDATE, IMPORTANT, READCHECK) SELECT PM_SENDMSG_SEQ.NEXTVAL, A.* FROM(
SELECT #{msgList.to_email}, 'admin', #{msgList.content}, SYSDATE, NULL, 1, 0 FROM DUAL
) A

INSERT INTO PM_REVMSG(SEQ, TO_EMAIL, FROM_EMAIL, CONTENT, SENDDATE, READDATE, IMPORTANT, READCHECK) SELECT PM_REVMSG_SEQ.NEXTVAL, A.* FROM(
SELECT #{msgList.to_email}, 'admin', #{msgList.content}, SYSDATE, NULL, 1, 0 FROM DUAL
) A
