mapper.select:
SELECT SEQ, BOARD_CODE, EMAIL, TITLE, CONTENT, FILENAME, LOCATION, LOCATION_DETAIL, CATEGORY, PRICE, PEOPLE, PERSONCOUNT, READCOUNT, LIKECOUNT, REPORTCOUNT, PDATE, EDATE, REGDATE, DEL FROM (SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC)AS RNUM, SEQ, BOARD_CODE, EMAIL, TITLE, CONTENT, FILENAME, LOCATION, LOCATION_DETAIL, CATEGORY, PRICE, PEOPLE, PERSONCOUNT, READCOUNT, LIKECOUNT, REPORTCOUNT, PDATE, EDATE, REGDATE, DEL FROM PM_PLAYBOARD WHERE 1=1 AND CATEGORY LIKE '%'||#{playCategory}||'%'
)
AND DEL=1)
AND PDATE <= SYSDATE)
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT * FROM(SELECT ROW_NUMBER()OVER(ORDER BY REGDATE DESC)AS RNUM, SEQ, BOARD_CODE, EMAIL, TITLE, INTRO, CONTENT, THUMBNAIL, MAX_PRICE, CURRENT_PRICE, PERSONCOUNT, LIKECOUNT, READCOUNT, SDATE, EDATE, REGDATE FROM PM_FUNDINGBOARD WHERE 1=1
AND EDATE >= SYSDATE)
AND EDATE <= SYSDATE)
)A
WHERE RNUM BETWEEN ${start} AND ${end}
